
public class MathsFunctions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a, b, z;
		a = 3; b = 4; z = -67;
		System.out.println("Math.abs(z) : " + (Math.abs(z)));
		System.out.println(" Math.PI : " + (Math.PI));
		System.out.println(" Math.ceil(34.67) : " + (Math.ceil(34.67)));
		System.out.println(" Math.floor(34.67) : " + (Math.floor(34.67)));
		System.out.println("Math.pow(2,4) : " + (Math.pow(2,4)));
		System.out.println("Math.exp(1) : " + (Math.exp(1)));
		
		System.out.println("Math.sin(30) : " + (Math.sin(30)));
		System.out.println("Math.cos(30) : " + (Math.cos(30)));
		System.out.println("Math.tan(30) : " + (Math.tan(30)));
		
		System.out.println("Math.asin(0.5) : " + (Math.asin(0.5)));
		System.out.println("Math.acos(0.5) : " + (Math.acos(0.5)));
		System.out.println("Math.atan(0.5) : " + (Math.atan(0.5)));
		

	}

}
